mapper.select:
SELECT 

FROM medsto_ypthzd a

WHERE a.id = #{id}

SELECT 

FROM medsto_ypthzd a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.xh = #{xh}


		AND a.djh = #{djh}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM medsto_ypthzd a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE medsto_ypthzd SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
